import React, { useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { getCart } from '../api/api';

const Cart = ({ userId }) => {
  const { cart, removeFromCart, total, calculateTotal } = useCart();

  // Fetch the cart data when the component mounts
  useEffect(() => {
    const fetchCart = async () => {
      const cartData = await getCart(userId);
      console.log(cartData); // You can add code to set the cart data in the context
    };
    fetchCart();
  }, [userId]); // Only fetch when userId changes

  // Recalculate total when cart items change
  useEffect(() => {
    calculateTotal();
  }, [cart, calculateTotal]); // Include calculateTotal in the dependencies

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cart.map((item) => (
        <div key={item.productId}>
          <h3>{item.product.name}</h3>
          <p>Quantity: {item.quantity}</p>
          <button onClick={() => removeFromCart(item.productId)}>Remove</button>
        </div>
      ))}
      <h3>Total: ${total}</h3>
    </div>
  );
};

export default Cart;
