import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/', // Update this with your backend URL if deployed
});

export const getProducts = async () => {
  const response = await api.get('/products');
  return response.data;
};

export const addToCart = async (userId, productId, quantity) => {
  await api.post('/cart', { userId, productId, quantity });
};

export const getCart = async (userId) => {
  const response = await api.get(`/cart?userId=${userId}`);
  return response.data;
};

export const updateQuantity = async (userId, productId, quantity) => {
  await api.put('/cart', { userId, productId, quantity });
};

export const removeFromCart = async (userId, productId) => {
  await api.delete('/cart', { data: { userId, productId } });
};
