import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const ProductList = ({ userId }) => {
  const { products, handleAddToCart } = useCart(); // Get products and handleAddToCart from context
  const [quantity, setQuantity] = useState(1); // Local state for quantity selection

  // Function to handle "Add to Cart" button click
  const handleAddClick = (productId) => {
    if (quantity < 1) {
      alert("Quantity must be at least 1");
      return;
    }
    handleAddToCart(userId, productId, quantity);
    setQuantity(1); // Reset the quantity input after adding to cart
  };

  return (
    <div className="product-list">
      <h2>Product List</h2>
      <div className="product-container">
        {products.length === 0 ? (
          <p>No products available</p>
        ) : (
          products.map((product) => (
            <div key={product._id} className="product-card">
              <img src={product.imageUrl || "https://via.placeholder.com/150"} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>${product.price}</p>
              <div className="quantity-container">
                <input
                  type="number"
                  value={quantity}
                  min="1"
                  onChange={(e) => setQuantity(e.target.value)}
                  className="quantity-input"
                />
                <button onClick={() => handleAddClick(product._id)}>Add to Cart</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProductList;
